/**
  * @summary `z = (x ** y)`
  * @function
  * @name pow
  * @alias power
  * @param {number} value number
  * @param {number} next number
  * @returns {number} number
  * @example
  * ```
  * pow(1, 2); // 1
  * pow(2, 1); // 2
  * pow(1, 1); // 1
  * pow(2, 2); // 4
  * pow(0, 2); // 0
  * pow(2, 3); // 6
  * ```
  */
export default function pow(value: number, next: number): number;
