type Add2<Value>  = Inc<Inc<Value>>;
type Add3<Value>  = Inc<Add2<Value>>;
type Add4 <Value> = Add2<Add2<Value>>;
type Add5 <Value> = Add2<Add3<Value>>;
type Add6 <Value> = Add3<Add3<Value>>;
type Add7 <Value> = Add3<Add4<Value>>;
type Add8 <Value> = Add4<Add4<Value>>;
type Add9 <Value> = Add4<Add5<Value>>;
type Add10<Value> = Value extends keyof Add10
  ? Add10[Value]
  : number
;

type Addition<X extends number, Y extends number> =
    Y extends 1  ? Inc<X>
  : Y extends 2  ? Add2<X>
  : Y extends 3  ? Add3<X>
  : Y extends 4  ? Add4<X>
  : Y extends 5  ? Add5<X>
  : Y extends 6  ? Add6<X>
  : Y extends 7  ? Add7<X>
  : Y extends 8  ? Add8<X>
  : Y extends 9  ? Add9<X>
  : Y extends 10 ? Add10<X>
  : Y extends 11 ? Add10<Inc<X>>
  : Y extends 12 ? Add10<Add2<X>>
  : Y extends 13 ? Add10<Add3<X>>
  : Y extends 14 ? Add10<Add4<X>>
  : Y extends 15 ? Add10<Add5<X>>
  : number
;

